import { HttpErrorResponse } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface ISportsList
{
    getSports(): Observable<any>;

    addSport(sport: any): Observable<any>;

    deleteSportsById(id: any): Observable<any>;

    updateSport(sport: any): Observable<any>;

    getLoginCheck(user: any): Observable<any>;

    addLogin(userLogin: any): Observable<any>;

    handleError(error: HttpErrorResponse): Observable<never>;
}

export interface User {
    username?: string;
    password?: string;
}

export interface Sport {
    id?: string;
    sportsTitle?: string;
    description?: string;
    category?: string;
}

import { User, Sport } from '../model/user';
import {
  AuthActionTypes, AuthAction
} from '../actions/auth.action';

export interface State {
  // is a user authenticated?
  isAuthenticated: boolean;
  // if authenticated, there should be a user object
  user: User | null;
  // error message
  errorMessage: string | null;
}

export const initialState: State = {
  isAuthenticated: false,
  user: null,
  errorMessage: null,
};

export function reducer(state = initialState, action: AuthAction): State {
  switch (action.type) {
    case AuthActionTypes.LOGIN_SUCCESS: {
      return {
        ...state,
        isAuthenticated: true,
        user: {
          username: action.payload.username,
          password: action.payload.password
        },
        errorMessage: null
      };
    }
    case AuthActionTypes.LOGIN_FAILURE: {
      return {
        ...state,
        errorMessage: 'Incorrect email and/or password.'
      };
    }
    case AuthActionTypes.SIGNUP_SUCCESS: {
      return {
        ...state,
        isAuthenticated: false,
        user: {
          username: action.payload.username
        },
        errorMessage: null
      };
    }
    case AuthActionTypes.SIGNUP_FAILURE: {
      return {
        ...state,
        errorMessage: 'The user is already Registered.',
      };
    }
    case AuthActionTypes.LOGOUT: {
      return;
    }
    default: {
      return state;
    }
  }
}

